    private void calcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_calcularActionPerformed
        // TODO add your handling code here:
        String varSoma;
        int total = 0;
        int vn1, vn2, vn3, vn4, vn5, vn6, vn7, vn8, vn9, vn10;

        try {
            vn1 = Integer.parseInt(n1.getText());
            if (vn1 % 2 == 0) {
                total = total + vn1;
            }
            vn2 = Integer.parseInt(n2.getText());
            if (vn2 % 2 == 0) {
                total = total + vn2;
            }
            vn3 = Integer.parseInt(n3.getText());
            if (vn3 % 2 == 0) {
                total = total + vn3;
            }
            vn4 = Integer.parseInt(n4.getText());
            if (vn4 % 2 == 0) {
                total = total + vn4;
            }
            vn5 = Integer.parseInt(n5.getText());
            if (vn5 % 2 == 0) {
                total = total + vn5;
            }
            vn6 = Integer.parseInt(n6.getText());
            if (vn6 % 2 == 0) {
                total = total + vn6;
            }
            vn7 = Integer.parseInt(n7.getText());
            if (vn7 % 2 == 0) {
                total = total + vn7;
            }
            vn8 = Integer.parseInt(n8.getText());
            if (vn8 % 2 == 0) {
                total = total + vn8;
            }
            vn9 = Integer.parseInt(n9.getText());
            if (vn9 % 2 == 0) {
                total = total + vn9;
            }
            vn10 = Integer.parseInt(n10.getText());
            if (vn10 % 2 == 0) {
                total = total + vn10;
            }
            soma.setText("" + total);

        } catch (Exception e) {
            soma.setText("Formato Inv√°lido");
        }
    }//GEN-LAST:event_calcularActionPerformed

